mapper.insert:
insert into user_force (id, user_id, gold, 
  user_force, user_medal, state, 
  create_time, time_state, update_time
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{gold,jdbcType=INTEGER}, 
  #{userForce,jdbcType=INTEGER}, #{userMedal,jdbcType=VARCHAR}, #{state,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{timeState,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT *
FROM user_force
WHERE 1=1

  and user_id = #{userId,jdbcType=INTEGER}


  and gold = #{gold,jdbcType=INTEGER}


  and user_force = #{userForce,jdbcType=INTEGER}


  and user_medal = #{userMedal,jdbcType=VARCHAR}


  and state = #{state,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and time_state = #{timeState,jdbcType=INTEGER}


 and update_time = #{updateTime,jdbcType=TIMESTAMP}


  limit #{start},#{pageSize}

SELECT count(*)
FROM user_force
WHERE 1=1

  and user_id = #{userId,jdbcType=INTEGER}


  and gold = #{gold,jdbcType=INTEGER}


  and user_force = #{userForce,jdbcType=INTEGER}


  and user_medal = #{userMedal,jdbcType=VARCHAR}


  and state = #{state,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and time_state = #{timeState,jdbcType=INTEGER}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}
